mapper.select:

select id, name
  from user
 where id = #{id }
   and password = #{password }


select no, name, email, gender 
  from user
 where no = #{no }


select no, name 
  from user
 where email = #{email }
   and password = #{password }


select id
  from user
 where id = #{id }

mapper.update:
		
        update user
           set name = #{name },
                    
           
			
               password = #{password },
			
               
		
               gender = #{gender }
         where no = #{no }
